    def updateDeptTable(self,data):
        self.dept_data_thread.retrievalComplete.connect(self.dept_data_thread.quit)
        for row_number, row_data in enumerate(data):
            self.tableDeptsDet.insertRow(row_number)
            for column_number, column_data in enumerate(row_data):
                item = QtWidgets.QTableWidgetItem(str(column_data))
                self.tableDeptsDet.setItem(row_number, column_number, item)

    def showWarningMessage(self,message):
        msgBox = QtWidgets.QMessageBox()
        msgBox.setIcon(QtWidgets.QMessageBox.Warning)
        msgBox.setWindowTitle("Warning")
        msgBox.setText(message)
        msgBox.setStandardButtons(QtWidgets.QMessageBox.Ok)
        msgBox.exec_()

    def verifyData(self,data):
        if not checkEmptyFields(data):
                self.showWarningMessage("All Fields are mandatory!!")
        else:
                if checkNumericalData(data[0]) and checkNumericalData(data[2]) and checkNumericalData(data[3]) and checkNumericalData(data[4]):
                        if checkLicenseNumber(data[0],self.tableDeptsDet):
                             return True
                        else:
                             self.showWarningMessage("Dept id already in use!!")
                else:
                     self.showWarningMessage("Number field error!!")
        return False

    def addData(self):
        data = (self.deptIdEntry.toPlainText().strip(),self.deptNameEntry.toPlainText().strip(),
                self.deptDocCountEntry.toPlainText().strip(),self.deptNursesCountEntry.toPlainText().strip(),
                self.deptStaffCountEntry.toPlainText().strip(),self.deptEstdDateEntry.toPlainText().strip(),
                self.deptStatusEntry.toPlainText().strip())
        if self.verifyData(data):
                self.dept_data_thread = AddUpdateDelDeptDataThread(data,flag=1)
                self.dept_data_thread.start()
                self.dept_data_thread.operationComplete.connect(self.refreshTable)
        else:
             self.showWarningMessage("Operation failed!!")

    def verifyUpdtData(self,data):
        if not checkEmptyFields(data):
                self.showWarningMessage("All Fields are mandatory!!")
        else:
                if checkNumericalData(data[0]) and checkNumericalData(data[2]) and checkNumericalData(data[3]) and checkNumericalData(data[4]):
                        row = getSelectedRowData(self.tableDeptsDet)
                        if row is not None and str(row[0]) == str(data[0]):
                                return True
                        else:
                                self.showWarningMessage("Please select correct record!!")
                else:
                     self.showWarningMessage("Number field error!!")

    def updtData(self):
        data = (self.deptIdEntry.toPlainText().strip(),self.deptNameEntry.toPlainText().strip(),
                self.deptDocCountEntry.toPlainText().strip(),self.deptNursesCountEntry.toPlainText().strip(),
                self.deptStaffCountEntry.toPlainText().strip(),self.deptEstdDateEntry.toPlainText().strip(),
                self.deptStatusEntry.toPlainText().strip())
        if self.verifyUpdtData(data):
                row = getSelectedRowData(self.tableDeptsDet)
                data = (self.deptIdEntry.toPlainText().strip(),self.deptNameEntry.toPlainText().strip(),
                self.deptDocCountEntry.toPlainText().strip(),self.deptNursesCountEntry.toPlainText().strip(),
                self.deptStaffCountEntry.toPlainText().strip(),self.deptEstdDateEntry.toPlainText().strip(),
                self.deptStatusEntry.toPlainText().strip(),row[0])
                self.dept_data_thread = AddUpdateDelDeptDataThread(data,flag=2)
                self.dept_data_thread.start()
                self.dept_data_thread.operationComplete.connect(self.refreshTable)
        else:
             self.showWarningMessage("Operation failed!!")

    def delData(self):
        row = getSelectedRowData(self.tableDeptsDet)
        if row is not None:
                selected_id_no = row[0]
                self.dept_data_thread = AddUpdateDelDeptDataThread(selected_id_no,flag=3)
                self.dept_data_thread.start()
                self.dept_data_thread.operationComplete.connect(self.refreshTable)
        else:
             self.showWarningMessage("Please select record!!")

    def refreshTable(self):
        self.tableDeptsDet.clearContents()
        self.tableDeptsDet.setRowCount(0)
        self.dept_data_thread = DeptDataRetrievalThread()
        self.dept_data_thread.dataRetrieved.connect(self.updateDeptTable)
        self.dept_data_thread.start()

