from clickableFrame import ClickableFrame
from database import ConnectionPool,showEmergencyBedAllData,updateEmergencyBedsData
from EntryChecking import checkNumericWith0,checkEmptyFields

class EmerBedDataRetrievalThread(QtCore.QThread):
    dataRetrieved = QtCore.pyqtSignal(object)
    retrievalComplete = QtCore.pyqtSignal()
    def __init__(self, parent=None):
        super(EmerBedDataRetrievalThread, self).__init__(parent)

    def run(self):
        try:
                pool = ConnectionPool(5, 'hms_database.db')
                data = showEmergencyBedAllData(pool)
                self.dataRetrieved.emit(data)
        except Exception as e:
            print(e)

class UpdateBedDataThread(QtCore.QThread):
    operationComplete = QtCore.pyqtSignal()
    def __init__(self,data,parent=None):
        super(UpdateBedDataThread, self).__init__(parent)
        self.data=data
    def run(self):
        try:
                pool = ConnectionPool(5, 'hms_database.db')
                updateEmergencyBedsData(pool,self.data)
                self.operationComplete.emit()

        except Exception as e:
            print(e)

