    def updateBedsTable(self,data):
        self.beds_data_retrieval_thread.retrievalComplete.connect(self.beds_data_retrieval_thread.quit)
        for row_number, row_data in enumerate(data):
            self.tableBedsDet.insertRow(row_number)
            for column_number, column_data in enumerate(row_data):
                item = QtWidgets.QTableWidgetItem(str(column_data))
                self.tableBedsDet.setItem(row_number, column_number, item)
   
    def showWarningMessage(self,message):
        msgBox = QtWidgets.QMessageBox()
        msgBox.setIcon(QtWidgets.QMessageBox.Warning)
        msgBox.setWindowTitle("Warning")
        msgBox.setText(message)
        msgBox.setStandardButtons(QtWidgets.QMessageBox.Ok)
        msgBox.exec_()

    def updateData(self):
        data = (self.totBedsEntry.toPlainText().strip(),str(0),self.totBedsEntry.toPlainText().strip())
        if checkNumericWith0(self.totBedsEntry.toPlainText().strip()) and checkEmptyFields(data):
            msg_box = QtWidgets.QMessageBox()
            msg_box.setIcon(QtWidgets.QMessageBox.Warning)
            msg_box.setText("This will clear patient data and update total number of beds.\nProceed?")
            msg_box.setWindowTitle("Warning")
            msg_box.setStandardButtons(QtWidgets.QMessageBox.Ok | QtWidgets.QMessageBox.Cancel)
            result = msg_box.exec_()
            if result == QtWidgets.QMessageBox.Ok:
                self.bed_updt_thread = UpdateBedDataThread(data)
                self.bed_updt_thread.start()
                self.bed_updt_thread.operationComplete.connect(self.refreshTable)
            else:
                pass
        else:
            self.showWarningMessage("Numerical value error!!")
    
    def refreshTable(self):
        self.tableBedsDet.clearContents()
        self.tableBedsDet.setRowCount(0)
        self.beds_data_retrieval_thread = EmerBedDataRetrievalThread()
        self.beds_data_retrieval_thread.dataRetrieved.connect(self.updateBedsTable)
        self.beds_data_retrieval_thread.start()

