    def updatePharmaTable(self,data):
        self.pharma_data_thread.retrievalComplete.connect(self.pharma_data_thread.quit)
        for row_number, row_data in enumerate(data):
            self.tablePharmsDet.insertRow(row_number)
            for column_number, column_data in enumerate(row_data):
                item = QtWidgets.QTableWidgetItem(str(column_data))
                self.tablePharmsDet.setItem(row_number, column_number, item)
    
