from database import showDeptsData,ConnectionPool

class DeptsDataRetrievalThread(QtCore.QThread):
    dataRetrieved = QtCore.pyqtSignal(object)
    retrievalComplete = QtCore.pyqtSignal()
    def __init__(self,parent=None):
        super(DeptsDataRetrievalThread, self).__init__(parent)

    def run(self):
        try:
                pool = ConnectionPool(5, 'hms_database.db')
                data = showDeptsData(pool)
                self.dataRetrieved.emit(data)
                self.retrievalComplete.emit()
        except Exception as e:
            print(e)

